(function($){$.fn.YWPlaceholder=function(color){var color=color||"#999";if("placeholder" in document.createElement("input")){return false}return this.each(function(i,obj){var YWPlaceholder=(this.name||this.id).replace(".","_")+"_YWPlaceholder_"+i;var _this=$(this);var className=_this.attr("class")!=undefined?_this.attr("class"):"";var placeholderText=_this.attr("placeholder");_this.before("<input type='text' id="+YWPlaceholder+" class='"+className+"' value='"+placeholderText+"'>");$("#"+YWPlaceholder).hide().css("color",color);if(this.value==""){_this.hide();$("#"+YWPlaceholder).show()}$("#"+YWPlaceholder).focus(function(){$(this).hide();_this.show();_this.focus()});_this.blur(function(){if(this.value==""){_this.hide();$("#"+YWPlaceholder).show()}})})}})(jQuery);